import React from "react";
import settings from "../../../src/assets/Icons/settings.png";
import eth from "../../../src/assets/Icons/eth.png";

import { FileArrowDown } from "react-bootstrap-icons";
import { Button } from "react-bootstrap";
import TokenModal from "../Modals/TokenModal";
export default function Swap() {
  const [modalShow, setModalShow] = React.useState(false);
  const token1 = {
    name: "ETH",
    icon: eth,
  };

  const tokenDetails = [
    {
      name: "ETH",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
  ];

  const [selectedToken, setSelectedToken] = React.useState(token1);
  const [selectedToken2, setSelectedToken2] = React.useState({});

  return (
    // center div with glassmorphism
    <div
      className="container"
      style={{
        height: "40vh",
        width: "20vw",
        background: "rgba(5, 24, 57, 255)",
        //add simple glass effect
        boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37)",
        backdropFilter: "blur(4px)",
        borderRadius: "20px",
        border: "0.5px solid white ",
      }}>
      <div className="row my-3">
        <div className="col-8">
          <h3
            style={{
              color: "#f8fdfd",
            }}>
            music-exchanger
          </h3>
        </div>

        <div className="col-4">
          <img
            alt="settings"
            src={settings}
            style={{
              height: "40px",
              width: "40px",
            }}
          />
        </div>
      </div>

      <div
        className="row "
        style={{
          justifyContent: "center",
          alignItems: "center",
        }}>
        <div
          className="col-12 my-3"
          style={{
            backgroundColor: "#00214e",
            borderRadius: "20px",
            height: "70px",
            width: "90%",
            border: "1px solid rgba(255, 255, 255, 0.2)",
          }}>
          <div className="row">
            <div className="col-8">
              <input
                className="col-7"
                type="text"
                placeholder="0.0"
                style={{
                  marginTop: "5px",
                  marginLeft: "-80px",
                  backgroundColor: "transparent",
                  border: "none",
                  color: "white",
                  fontSize: "30px",
                  outline: "none",
                  placeholder: "white",
                }}
                onFocus={(e) => (e.target.style.outline = "none")}
              />
            </div>

            <div onClick={() => setModalShow(true)} className="col-4">
              <div
                style={{
                  backgroundColor: "#183964",
                  borderRadius: "25px",
                  height: "40px",
                  width: "120px",
                  marginTop: "15px",
                  paddingTop: "2px",
                }}
                className="row">
                <div className="col-3">
                  <img
                    alt="coin"
                    src={eth}
                    style={{
                      height: "35px",
                      width: "35px",
                    }}
                  />
                </div>
                <div className="my-1 mx-1 col-3">
                  <p>
                    <h5 className="text-light">{selectedToken.name}</h5>
                  </p>
                </div>

                <div className="mx-2 my-1 col-3">
                  <FileArrowDown size={25} color="white" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <TokenModal
        tokenDetails={tokenDetails}
        show={modalShow}
        onHide={() => setModalShow(false)}
        selectedToken={selectedToken}
      />

      <div
        className="row "
        style={{
          justifyContent: "center",
          alignItems: "center",
        }}>
        <div
          className="col-12 "
          style={{
            backgroundColor: "#00214e",
            borderRadius: "20px",
            height: "70px",
            width: "90%",
            border: "1px solid rgba(255, 255, 255, 0.2)",
          }}>
          <div className="row">
            <div className="col-8">
              <input
                className="col-7"
                type="text"
                placeholder="0.0"
                style={{
                  marginTop: "5px",
                  marginLeft: "-80px",
                  backgroundColor: "transparent",
                  border: "none",
                  color: "white",
                  fontSize: "30px",
                  outline: "none",
                  placeholder: "white",
                }}
                onFocus={(e) => (e.target.style.outline = "none")}
              />
            </div>

            <div className="col-4">
              <div
                style={{
                  backgroundColor: "#183964",
                  borderRadius: "25px",
                  height: "40px",
                  width: "120px",
                  marginTop: "15px",
                  paddingTop: "2px",
                }}
                className="row">
                <div className="col-3">
                  <img
                    alt="coin"
                    src={eth}
                    style={{
                      height: "35px",
                      width: "35px",
                    }}
                  />
                </div>
                <div className="my-1 mx-1 col-3">
                  <p>
                    <h5 className="text-light">{selectedToken.name}</h5>
                  </p>
                </div>

                <div className="mx-2 my-1 col-3">
                  <FileArrowDown size={25} color="white" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Button
        variant="primary"
        className="mx-5 my-5"
        style={{
          width: "75%",
          height: "50px",
          borderRadius: "20px",
          backgroundColor: "#006dfe",
          fontSize: "25px",
        }}>
        <h5 className="text-light">Change</h5>
      </Button>
    </div>
  );
}
