import React, { useState } from "react";
import TopSongsHeader from "./TopSongsHeader";
import eth from "../../../src/assets/Icons/eth.png";

import "rsuite/dist/rsuite.min.css";
import Select from "react-select";

export default function Songs() {
  const data = [
    {
      value: 1,
      text: "MUD",
      icon: (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="16"
          height="16"
          fill="currentColor"
          class="bi bi-arrow-up-circle"
          viewBox="0 0 16 16">
          <path
            fill-rule="evenodd"
            d="M1 8a7 7 0 1 0 14 0A7 7 0 0 0 1 8zm15 0A8 8 0 1 1 0 8a8 8 0 0 1 16 0zm-7.5 3.5a.5.5 0 0 1-1 0V5.707L5.354 7.854a.5.5 0 1 1-.708-.708l3-3a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1-.708.708L8.5 5.707V11.5z"
          />
        </svg>
      ),
    },
    {
      value: 2,
      text: "BTX",
      icon: (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="16"
          height="16"
          fill="currentColor"
          class="bi bi-arrow-down-circle"
          viewBox="0 0 16 16">
          <path
            fill-rule="evenodd"
            d="M1 8a7 7 0 1 0 14 0A7 7 0 0 0 1 8zm15 0A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM8.5 4.5a.5.5 0 0 0-1 0v5.793L5.354 8.146a.5.5 0 1 0-.708.708l3 3a.5.5 0 0 0 .708 0l3-3a.5.5 0 0 0-.708-.708L8.5 10.293V4.5z"
          />
        </svg>
      ),
    },
    {
      value: 3,
      text: "BXG",
      icon: (
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="16"
          height="16"
          fill="currentColor"
          class="bi bi-arrow-left-circle"
          viewBox="0 0 16 16">
          <path
            fill-rule="evenodd"
            d="M1 8a7 7 0 1 0 14 0A7 7 0 0 0 1 8zm15 0A8 8 0 1 1 0 8a8 8 0 0 1 16 0zm-4.5-.5a.5.5 0 0 1 0 1H5.707l2.147 2.146a.5.5 0 0 1-.708.708l-3-3a.5.5 0 0 1 0-.708l3-3a.5.5 0 1 1 .708.708L5.707 7.5H11.5z"
          />
        </svg>
      ),
    },
    {
      value: 4,
      text: "ETH",
      icon: (
        <img
          src={eth}
          alt="eth"
          style={{
            width: "20px",
            height: "20px",
            borderRadius: "50%",
            background: "white",
          }}
        />
      ),
    },
  ];

  const data2 = [
    {
      value: 1,
      text: "1D",
    },
    {
      value: 2,
      text: "1W",
    },
    {
      value: 3,
      text: "1M",
    },
    {
      value: 4,
      text: "1Y",
    },
    {
      value: 5,
      text: "ALL",
    },
  ];

  const [selectedOption, setSelectedOption] = useState(data[3]);
  const [selectedOptionforTime, setSelectedOptionforTime] = useState(data2[4]);
  const handleChange = (e) => {
    setSelectedOption(e);
  };

  const handleChangeforTime = (e) => {
    setSelectedOptionforTime(e);
  };
  return (
    <div
      className="col-12 col-sm-8  col-md-8 col-lg-8"
      style={{
        background: "rgba(5, 24, 57, 255)",
        boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37)",
        backdropFilter: "blur(4px)",

        borderRadius: "20px",
        border: "1px solid whitesmoke ",
        fontFamily: "sans-serif",
      }}>
      <TopSongsHeader header="Top Songs on Mudex" />
      <div className="row mx-1">
        <div className="col-2">
          <Select
            styles={{
              //change background color of dropdown
              menu: (provided, state) => ({
                ...provided,
                background: "rgba(5, 24, 57, 255)",
                color: "white",
              }),

              //change background of selected option
              option: (provided, state) => ({
                ...provided,
                background: state.isSelected
                  ? "rgba(5, 24, 57, 255)"
                  : "rgba(5, 24, 57, 255)",
                color: "white",
              }),

              //change background of whole dropdown
              control: (provided, state) => ({
                ...provided,

                color: "white",
                borderRadius: "15px",
                background: "#202c46",
              }),

              //change color of placeholder

              placeholder: (provided, state) => ({
                ...provided,
                color: "white",
              }),

              //change color of dropdown indicator
              dropdownIndicator: (provided, state) => ({
                ...provided,
                color: "white",
              }),

              //change color of selected option
              singleValue: (provided, state) => ({
                ...provided,
                color: "white",
              }),

              //remove border of indicator
              indicatorSeparator: (provided, state) => ({
                ...provided,
                display: "none",
              }),

              //set font size of selected option
              valueContainer: (provided, state) => ({
                ...provided,
                fontSize: "18px",
              }),
            }}
            placeholder="Select Option"
            value={selectedOption}
            options={data}
            onChange={handleChange}
            getOptionLabel={(e) => (
              <div style={{ display: "flex", alignItems: "center" }}>
                {e.icon}
                <span style={{ marginLeft: 5 }}>{e.text}</span>
              </div>
            )}
          />
        </div>
        <div className="col-1 mx-0">
          <Select
            styles={{
              //change background color of dropdown
              menu: (provided, state) => ({
                ...provided,
                background: "rgba(5, 24, 57, 255)",
                color: "white",
              }),

              //change background of selected option
              option: (provided, state) => ({
                ...provided,
                background: state.isSelected
                  ? "rgba(5, 24, 57, 255)"
                  : "rgba(5, 24, 57, 255)",
                color: "white",
              }),

              //change background of whole dropdown
              control: (provided, state) => ({
                ...provided,
                background: "#202c46",
                color: "white",
                borderRadius: "15px",
              }),

              //change color of placeholder

              placeholder: (provided, state) => ({
                ...provided,
                color: "white",
              }),

              //change color of dropdown indicator
              dropdownIndicator: (provided, state) => ({
                ...provided,
                color: "white",
              }),

              //change color of selected option
              singleValue: (provided, state) => ({
                ...provided,
                color: "white",
              }),

              //remove border of indicator
              indicatorSeparator: (provided, state) => ({
                ...provided,
                display: "none",
              }),

              //set font size of selected option
            }}
            placeholder="Select Option"
            value={selectedOptionforTime}
            options={data2}
            onChange={handleChangeforTime}
            getOptionLabel={(e) => (
              <div style={{ display: "flex", alignItems: "center" }}>
                <span style={{ marginLeft: 5 }}>{e.text}</span>
              </div>
            )}
          />
        </div>
        <div className="col-3 my-1">
          <input
            icon={eth}
            className="col-5"
            type="text"
            placeholder="Search"
            style={{
              background: "rgba(5, 24, 57, 255)",
              color: "white",
              border: "none",

              outline: "none",
              width: "100%",
              height: "100%",
            }}
          />
        </div>
      </div>
    </div>
  );
}
