import React from "react";
import settings from "../../../src/assets/Icons/settings.png";
import eth from "../../../src/assets/Icons/eth.png";

import { FileArrowDown } from "react-bootstrap-icons";
import { Button } from "react-bootstrap";
import TokenModal from "../Modals/TokenModal";
import arrow from "../../../src/assets/Icons/swap.png";
export default function Swap() {
  const [modalShow, setModalShow] = React.useState(false);
  const [modalShow2, setModalShow2] = React.useState(false);

  const tokenDetails = [
    {
      name: "ETH",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "BTX",
      icon: eth,
    },
    {
      name: "BXG",
      icon: eth,
    },
    {
      name: "WETH",
      icon: eth,
    },
    {
      name: "ZAR",
      icon: eth,
    },
    {
      name: "USD",
      icon: eth,
    },
    {
      name: "XTG",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
  ];

  const swap = () => {
    //swap logic

    const temp = selectedToken;
    setSelectedToken(selectedToken2);
    setSelectedToken2(temp);
  };

  const [selectedToken, setSelectedToken] = React.useState(tokenDetails[0]);
  const [selectedToken2, setSelectedToken2] = React.useState(tokenDetails[2]);

  return (
    // center div with glassmorphism
    <div
      className="col-12 col-md-6 col-lg-3"
      style={{
        background: "rgba(5, 24, 57, 255)",
        boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37)",
        backdropFilter: "blur(4px)",
        borderRadius: "20px",
        border: "0.5px solid white ",
        fontFamily: "sans-serif",
      }}>
      <div
        style={{
          position: "absolute",
        }}>
        <div
          className="row my-3"
          style={{
            justifyContent: "space-between",
          }}>
          <div className="col-9 ">
            <h3
              style={{
                color: "#f8fdfd",
                marginRight: "150px",
              }}>
              music-exchanger
            </h3>
          </div>

          <div className="col-2 mx-1">
            <img className="col-8" alt="settings" src={settings} />
          </div>
        </div>

        <div
          className="row my-1 "
          style={{
            justifyContent: "center",
            alignItems: "center",
            marginTop: "50px",
          }}>
          <div
            className="col-12 "
            style={{
              backgroundColor: "#00214e",
              borderRadius: "20px",
              height: "80px",
              width: "90%",
              border: "1px solid rgba(255, 255, 255, 0.2)",
            }}>
            <div className="row">
              <div className="col-8">
                <input
                  type="text"
                  placeholder="0.0"
                  style={{
                    marginTop: "13px",

                    backgroundColor: "transparent",
                    border: "none",
                    color: "white",
                    fontSize: "30px",
                    outline: "none",
                    placeholder: "white",
                  }}
                  onFocus={(e) => (e.target.style.outline = "none")}
                />
              </div>

              <div onClick={() => setModalShow(true)} className="col-4">
                <div
                  className="row"
                  style={{
                    backgroundColor: "#183964",
                    borderRadius: "25px",
                    height: "40px",

                    marginTop: "15px",
                    paddingTop: "2px",
                  }}>
                  <div className="col-3 ">
                    <img
                      alt="coin"
                      src={eth}
                      style={{
                        height: "35px",
                        width: "35px",
                      }}
                    />
                  </div>
                  <div className="my-1  col-4">
                    <p>
                      <h5 className="text-light">{selectedToken.name}</h5>
                    </p>
                  </div>

                  <div className="my-1 col-5">
                    <FileArrowDown size={25} color="white" />
                  </div>
                </div>
                <p
                  style={{
                    color: "lightgray",
                  }}>
                  Balance : $0.0
                </p>
              </div>
            </div>
          </div>
        </div>
        <TokenModal
          tokenDetails={tokenDetails}
          show={modalShow}
          onHide={() => setModalShow(false)}
          setSelectedToken={setSelectedToken}
        />
      </div>

      <Button
        className=""
        onClick={swap}
        style={{
          //transparent
          backgroundColor: "transparent",
          borderRadius: "75px",
        }}>
        Swap
      </Button>

      <div
        className="row my-1"
        style={{
          justifyContent: "center",
          alignItems: "center",
        }}>
        <div
          className="col-12 "
          style={{
            backgroundColor: "#00214e",
            borderRadius: "20px",
            height: "80px",
            width: "90%",
            border: "1px solid rgba(255, 255, 255, 0.2)",
          }}>
          <div className="row">
            <div className="col-8">
              <input
                type="text"
                placeholder="0.0"
                style={{
                  marginTop: "13px",

                  backgroundColor: "transparent",
                  border: "none",
                  color: "white",
                  fontSize: "30px",
                  outline: "none",
                  placeholder: "white",
                }}
                onFocus={(e) => (e.target.style.outline = "none")}
              />
            </div>

            <div
              onClick={() => {
                setModalShow2(true);
              }}
              className="col-4">
              <div
                style={{
                  backgroundColor: "#183964",
                  borderRadius: "20px",
                  height: "40px",
                  marginRight: "0.1px",
                  marginTop: "15px",
                  paddingTop: "3px",
                }}
                className="row">
                <div className="col-3">
                  <img
                    alt="coin"
                    src={selectedToken2.icon}
                    style={{
                      height: "35px",
                      width: "35px",
                    }}
                  />
                </div>
                <div className="my-1 col-4">
                  <p>
                    <h5 className="text-light">{selectedToken2.name}</h5>
                  </p>
                </div>

                <div className=" my-1 col-5">
                  <FileArrowDown size={25} color="white" />
                </div>
              </div>
              <p
                style={{
                  color: "lightgray",
                }}>
                Balance : $0.0
              </p>
            </div>
          </div>
        </div>
      </div>

      <TokenModal
        tokenDetails={tokenDetails}
        show={modalShow2}
        onHide={() => setModalShow2(false)}
        setSelectedToken={setSelectedToken2}
      />
    </div>
  );
}
