import React from "react";
import TokenModal from "../Modals/TokenModal";
import { FileArrowDown } from "react-bootstrap-icons";

export default function SwapInput({
  setModalShow,
  setSelectedToken,
  selectedToken,
  tokenDetails,
  modalshow,
}) {
  return (
    <>
      <div className="row">
        <div className="col-7 col-xl-8 col-lg-8">
          <input
            type="number input-data"
            placeholder="0.0"
            style={{
              marginTop: "13px",
              textAlign: "left",
              backgroundColor: "transparent",
              border: "none",
              color: "white",
              fontSize: "30px",
              outline: "none",
              placeholder: "white",
            }}
            onFocus={(e) => (e.target.style.outline = "none")}
          />
        </div>

        <div
          onClick={() => {
            setModalShow(true);
          }}
          className="col-5 col-xl-4 col-lg-4">
          <div
            style={{
              backgroundColor: "#183964",
              borderRadius: "20px",
              height: "40px",
              marginRight: "0.1px",
              marginTop: "15px",
              paddingTop: "3px",
            }}
            className="row">
            <div className="col-3">
              <img
                alt="coin"
                src={selectedToken.icon}
                style={{
                  height: "35px",
                  width: "35px",
                }}
              />
            </div>
            <div className="my-1 col-4">
              <p>
                <h5 className="text-light">{selectedToken.name}</h5>
              </p>
            </div>

            <div className=" my-1 col-5">
              <FileArrowDown size={25} color="white" />
            </div>
          </div>
          <p
            style={{
              color: "lightgray",
            }}>
            Balance : $0.0
          </p>
        </div>
        <TokenModal
          tokenDetails={tokenDetails}
          show={modalshow}
          onHide={() => setModalShow(false)}
          setSelectedToken={setSelectedToken}
        />
      </div>
    </>
  );
}
