import { Button } from "react-bootstrap";
import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import { NavLink } from "react-router-dom";
import logo from "../../assets/logos/logo.png";

import {
  WalletDisconnectButton,
  WalletMultiButton,
} from "@solana/wallet-adapter-react-ui";
import { useConnection, useWallet } from "@solana/wallet-adapter-react";
import React, { useEffect, useState } from "react"; // Default styles that can be overridden by your app
require("@solana/wallet-adapter-react-ui/styles.css");

function NavigationBar(props) {
  // State

  const checkIfWalletIsConnected = async () => {
    if (window?.solana?.isPhantom) {
      console.log("Phantom wallet found!");
      const response = await window.solana.connect({ onlyIfTrusted: true });
      console.log("Connected with Public Key:", response.publicKey.toString());

      props.setWalletAddress(response.publicKey.toString());
    } else {
      alert("Solana object not found! Get a Phantom Wallet ðŸ‘»");
    }
  };

  const connectWallet = async () => {
    const { solana } = window;

    if (solana) {
      const response = await solana.connect();
      console.log("Connected with Public Key:", response.publicKey.toString());
      props.setWalletAddress(response.publicKey.toString());
    }
  };

  const renderNotConnectedContainer = () => (
    <Button
      variant="primary"
      size="sm"
      style={{ borderRadius: "20px" }}
      onClick={connectWallet}>
      Connect Wallet
    </Button>
  );

  // UseEffects
  useEffect(() => {
    const onLoad = async () => {
      await checkIfWalletIsConnected();
    };
    window.addEventListener("load", onLoad);
    return () => window.removeEventListener("load", onLoad);
  }, []);

  return (
    <Navbar
      collapseOnSelect
      fixed
      expand="lg"
      bg="transparent"
      variant="transparent">
      <Container>
        <Navbar.Brand className="mx-0" href="#home">
          <img
            src={logo}
            width="50"
            height="50"
            className="d-inline-block align-top"
            alt="Mudex logo"
          />
        </Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav" />
        <Navbar.Collapse id="responsive-navbar-nav">
          <Nav
            className="me-auto "
            style={{
              fontSize: "20px",
              fontWeight: "bold",
              fontFamily: "sans-serif",
            }}>
            {props.menu.map((item) => {
              return (
                <Nav.Link className="text-light mx-3">
                  <NavLink
                    to={item.path}
                    style={{
                      textDecoration: "none",
                      color: "white",
                    }}>
                    {item.title}
                  </NavLink>
                </Nav.Link>
              );
            })}
            <Nav.Link className="text-light">
              <input
                type="text"
                placeholder="Search Songs"
                style={{
                  marginLeft: "120px",
                  borderRadius: "20px",
                  height: "40px",
                  width: "300px",
                  //glassmorphism with black background

                  color: "white",

                  background: "rgba(5, 24, 57, 255)",

                  border: "1px solid rgba(255, 255, 255, 0.2)",
                  boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37)",
                  padding: "10px",
                  textAlign: "center",

                  //glassmorphism
                }}
              />
            </Nav.Link>
          </Nav>
          <Nav>
            {/*<Button*/}
            {/*    variant="primary"*/}
            {/*    size="sm"*/}
            {/*    style={{borderRadius: "20px"}}*/}
            {/*    onClick={connectWallet}>*/}
            {/*    Connect Wallet*/}
            {/*</Button>*/}
            {!props.walletAddress && renderNotConnectedContainer()}
            {props.walletAddress && renderConnectedContainer()}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavigationBar;
