import React from "react";
import settings from "../../../src/assets/Icons/settings.png";
import eth from "../../../src/assets/Icons/eth.png";
import arrowdown from "../../../src/assets/Icons/arrow-down.svg";
import ArrowDown, {
  ArrowBarDown,
  ArrowDownCircle,
  FileArrowDown,
} from "react-bootstrap-icons";
export default function Swap() {
  const token1 = {
    name: "ETH",
    symbol: "ETH",
  };

  const [selectedToken, setSelectedToken] = React.useState(token1);
  const [selectedToken2, setSelectedToken2] = React.useState({});

  return (
    // center div with glassmorphism
    <div
      className="container"
      style={{
        height: "40vh",
        width: "20vw",
        border: "1px solid rgba(255, 255, 255, 0.2)",
        backdropFilter: "blur(4px)",
        //rounded corners
        borderRadius: "30px",
      }}>
      <div className="row my-3">
        <div className="col-8">
          <h3 className="text-light">music-exchanger</h3>
        </div>

        <div className="col-4">
          <img
            alt="settings"
            src={settings}
            style={{
              height: "40px",
              width: "40px",
            }}
          />
        </div>
      </div>

      <div
        className="row "
        style={{
          justifyContent: "center",
          alignItems: "center",
        }}>
        <div
          className="col-12"
          style={{
            backgroundColor: "skyblue",
            borderRadius: "20px",
            height: "50px",
            width: "90%",
          }}>
          <div className="row">
            <div className="col-8">
              <input
                className="col-8"
                type="text"
                placeholder="0.0"
                style={{
                  marginTop: "5px",
                  marginLeft: "-80px",
                  backgroundColor: "transparent",
                  border: "none",
                  color: "white",
                  fontSize: "20px",
                  fontWeight: "bold",
                  outline: "none",
                  placeholder: "white",
                }}
                onFocus={(e) => (e.target.style.outline = "none")}
              />
            </div>

            <div className="col-4">
              <div
                style={{
                  backgroundColor: "darkblue",
                  borderRadius: "20px",
                  height: "40px",
                  width: "100px",
                  marginTop: "5px",
                  paddingTop: "5px",
                }}
                className="row">
                <div className="col-3">
                  <img
                    alt="coin"
                    src={selectedToken.symbol}
                    style={{
                      height: "25px",
                      width: "25px",
                    }}
                  />
                </div>
                <div className="my-1 col-3">
                  <p>
                    <h6 className="text-light">{selectedToken.name}</h6>
                  </p>
                </div>

                <div className="mx-2 col-3">
                  <FileArrowDown color="white" />
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
