import React, { useEffect } from "react";
import settings from "../../../src/assets/Icons/settings.png";
import eth from "../../../src/assets/Icons/eth.png";
import { Input, Popover, Radio, Modal, message } from "antd";
import { ArrowDownOutlined, SettingOutlined } from "@ant-design/icons";
import { FileArrowDown } from "react-bootstrap-icons";
import { Button } from "react-bootstrap";
import TokenModal from "../Modals/TokenModal";
import arrow from "../../../src/assets/Icons/swap.png";
export default function Swap(props) {
  const [modalShow, setModalShow] = React.useState(false);
  const [modalShow2, setModalShow2] = React.useState(false);

  useEffect(() => {
    console.log(props.walletAddress);
  }, [props.walletAddress]);

  const tokenDetails = [
    {
      name: "ETH",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "BTX",
      icon: eth,
    },
    {
      name: "BXG",
      icon: eth,
    },
    {
      name: "WETH",
      icon: eth,
    },
    {
      name: "ZAR",
      icon: eth,
    },
    {
      name: "USD",
      icon: eth,
    },
    {
      name: "XTG",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
    {
      name: "MUD",
      icon: eth,
    },
  ];

  const swap = () => {
    //swap logic

    const temp = selectedToken1;
    setSelectedToken1(selectedToken2);
    setSelectedToken2(temp);
  };

  const [selectedToken1, setSelectedToken1] = React.useState(tokenDetails[0]);
  const [selectedToken2, setSelectedToken2] = React.useState(tokenDetails[2]);

  const [slippage, setSlippage] = React.useState(2.5);

  const handleSlippage = (e) => {
    setSlippage(e.target.value);
  };

  const settingsContent = (
    <>
      <div>Slippage Tolerance</div>
      <div>
        <Radio.Group onChange={handleSlippage} value={slippage}>
          <Radio.Button value={0.5}>0.5%</Radio.Button>
          <Radio.Button value={2.5}>2.5%</Radio.Button>
          <Radio.Button value={5}>5%</Radio.Button>
        </Radio.Group>
      </div>
    </>
  );

  return (
    // center div with glassmorphism
    <div
      className="col-12 col-sm-8  col-md-6
       col-lg-6 col-xl-4"
      style={{
        background: "rgba(0, 0, 0, 0.44)",
        borderRadius: "16px",
        boxShadow: "0 4px 30px rgba(0, 0, 0, 0.1)",
        backdropFilter: "blur(0px)",
        border: "1px solid whitesmoke",

        height: "400px",

        fontFamily: "sans-serif",
      }}>
      <div>
        <div
          className="row my-3"
          style={{
            justifyContent: "space-between",
          }}>
          <div
            style={{
              textAlign: "left",
              paddingLeft: "30px",
            }}
            className="col-9  mx-1">
            <h4
              style={{
                color: "#f8fdfd",
                marginRight: "150px",
              }}>
              music-exchanger
            </h4>
          </div>

          <div className="col-2 mx-1">
            <Popover
              title="Settings"
              trigger="click"
              placement="bottomRight"
              content={settingsContent}>
              <SettingOutlined className="cog" />
            </Popover>
          </div>
        </div>

        <div
          style={{
            position: "absolute",
            justifyContent: "center",
          }}>
          <div
            className="row my-1"
            style={{
              justifyContent: "center",
              alignItems: "center",
            }}>
            <div
              className="col-12  col-sm-12 col-md-12 col-lg-12 col-xl-12"
              style={{
                backgroundColor: "#00214e",
                borderRadius: "20px",
                height: "80px",
                width: "90%",
                border: "1px solid rgba(255, 255, 255, 0.2)",
              }}>
              <div className="row">
                <div className="col-8">
                  <input
                    type="number"
                    placeholder="0.0"
                    style={{
                      marginTop: "13px",

                      backgroundColor: "transparent",
                      border: "none",
                      color: "white",
                      fontSize: "30px",
                      outline: "none",
                      placeholder: "white",
                    }}
                    onFocus={(e) => (e.target.style.outline = "none")}
                  />
                </div>

                <div
                  onClick={() => {
                    setModalShow(true);
                  }}
                  className="col-4">
                  <div
                    style={{
                      backgroundColor: "#183964",
                      borderRadius: "20px",
                      height: "40px",
                      marginRight: "0.1px",
                      marginTop: "15px",
                      paddingTop: "3px",
                    }}
                    className="row">
                    <div className="col-3">
                      <img
                        alt="coin"
                        src={selectedToken1.icon}
                        style={{
                          height: "35px",
                          width: "35px",
                        }}
                      />
                    </div>
                    <div className="my-1 col-4">
                      <p>
                        <h5 className="text-light">{selectedToken1.name}</h5>
                      </p>
                    </div>

                    <div className=" my-1 col-5">
                      <FileArrowDown size={25} color="white" />
                    </div>
                  </div>
                  <p
                    style={{
                      color: "lightgray",
                    }}>
                    Balance : $0.0
                  </p>
                </div>
              </div>
            </div>
          </div>
          <TokenModal
            tokenDetails={tokenDetails}
            show={modalShow}
            onHide={() => setModalShow(false)}
            setSelectedToken={setSelectedToken1}
          />

          <div
            className="row my-1"
            style={{
              justifyContent: "center",
              alignItems: "center",
            }}>
            <div
              className="col-12 "
              style={{
                backgroundColor: "#00214e",
                borderRadius: "20px",
                height: "80px",
                width: "90%",
                border: "1px solid rgba(255, 255, 255, 0.2)",
              }}>
              <div className="row">
                <div className="col-8">
                  <input
                    type="number"
                    placeholder="0.0"
                    style={{
                      marginTop: "13px",

                      backgroundColor: "transparent",
                      border: "none",
                      color: "white",
                      fontSize: "30px",
                      outline: "none",
                      placeholder: "white",
                    }}
                    onFocus={(e) => (e.target.style.outline = "none")}
                  />
                </div>

                <div
                  onClick={() => {
                    setModalShow2(true);
                  }}
                  className="col-4">
                  <div
                    style={{
                      backgroundColor: "#183964",
                      borderRadius: "20px",
                      height: "40px",
                      marginRight: "0.1px",
                      marginTop: "15px",
                      paddingTop: "3px",
                    }}
                    className="row">
                    <div className="col-3">
                      <img
                        alt="coin"
                        src={selectedToken2.icon}
                        style={{
                          height: "35px",
                          width: "35px",
                        }}
                      />
                    </div>
                    <div className="my-1 col-4">
                      <p>
                        <h5 className="text-light">{selectedToken2.name}</h5>
                      </p>
                    </div>

                    <div className=" my-1 col-5">
                      <FileArrowDown size={25} color="white" />
                    </div>
                  </div>
                  <p
                    style={{
                      color: "lightgray",
                    }}>
                    Balance : $0.0
                  </p>
                </div>
              </div>
            </div>
          </div>

          <TokenModal
            tokenDetails={tokenDetails}
            show={modalShow2}
            onHide={() => setModalShow2(false)}
            setSelectedToken={setSelectedToken2}
          />
        </div>

        <Button
          className=""
          onClick={swap}
          style={{
            backgroundColor: "rgba(5, 24, 57, 255)",
            borderRadius: "75px",
            position: "relative",
            marginTop: "65px",
            justifyContent: "center",
            alignItems: "center",
          }}>
          S
        </Button>
      </div>
      <div
        className="col-12"
        style={{
          position: "absolute",
          justifyContent: "center",
          alignItems: "center",
          marginTop: "50px",
        }}>
        <Button
          variant="primary"
          className=" my-5"
          onClick={swap}
          style={{
            width: "75%",
            height: "50px",
            borderRadius: "20px",
            backgroundColor: "#006dfe",
            fontSize: "25px",

            //transparent
          }}>
          Change
        </Button>
      </div>
    </div>
  );
}
